import React from "react";
import { Grid, Paper, Typography } from "@mui/material";

function StateMatrixDisplay({ state }) {
  return (
    <Grid container spacing={1} sx={{ maxWidth: 400, margin: "0 auto" }}>
      {state.map((row, i) => (
        <Grid item xs={12} key={i}>
          <Grid container spacing={1}>
            {row.map((cell, j) => (
              <Grid item xs={3} key={j}>
                <Paper
                  sx={{
                    p: 2,
                    textAlign: "center",
                    fontFamily: "monospace",
                    bgcolor: "#f5f5f5",
                  }}
                >
                  {cell !== undefined
                    ? cell.toString(16).padStart(2, "0")
                    : "00"}
                </Paper>
              </Grid>
            ))}
          </Grid>
        </Grid>
      ))}
    </Grid>
  );
}

export default StateMatrixDisplay;
