import React, { useState } from "react";
import {
  TextField,
  Button,
  Box,
  Typography,
  Paper,
  Radio,
  RadioGroup,
  FormControl,
  FormControlLabel,
} from "@mui/material";
import {
  encrypt as aesEncrypt,
  decrypt as aesDecrypt,
} from "../algorithms/AES/aes";
import EncryptionSteps from "./EncryptionSteps";

function CipherForm() {
  const [key, setKey] = useState("");
  const [text, setText] = useState("");
  const [result, setResult] = useState("");
  const [mode, setMode] = useState("encrypt");
  const [encryptionSteps, setEncryptionSteps] = useState([]);
  const [activeStep, setActiveStep] = useState(0);

  const isHexString = (str) => {
    const hexRegEx = /^[0-9a-fA-F]+$/;
    return hexRegEx.test(str.replace(/\s+/g, "")); // Ignore spaces when testing for valid hex string
  };

  const stringToHex = (str) => {
    return str
      .split("")
      .map((c) => c.charCodeAt(0).toString(16).padStart(2, "0"))
      .join("");
  };

  const hexToString = (hex) => {
    return hex
      .match(/.{1,2}/g)
      .map((byte) => String.fromCharCode(parseInt(byte, 16)))
      .join("");
  };

  const handleEncrypt = () => {
    let inputText = text.trim();
    let flag = false;
    // Convert text to hex if it's not already in hex format
    if (!isHexString(inputText)) {
      flag = true;
      inputText = stringToHex(inputText);
    }

    let inputKey = key.trim();
    // Convert key to hex if it's not already in hex format
    if (!isHexString(inputKey)) {
      inputKey = stringToHex(inputKey);
    }

    let output;
    if (mode === "encrypt") {
      const result = aesEncrypt(inputText, inputKey);
      output = result.ciphertext;
      setEncryptionSteps(result.steps);
    } else {
      output = aesDecrypt(inputText, inputKey);
    }

    if (flag) {
      setResult(hexToString(output));
    } else {
      setResult(output);
    }
  };

  return (
    <Box sx={{ width: "100%", p: 3 }}>
      <Typography variant="h4" gutterBottom>
        AES Encryption
      </Typography>
      <TextField
        label="Input Text"
        value={text}
        onChange={(e) => setText(e.target.value)}
        fullWidth
        margin="normal"
      />
      <TextField
        label="Key"
        value={key}
        onChange={(e) => setKey(e.target.value)}
        fullWidth
        margin="normal"
      />
      <FormControl component="fieldset" sx={{ mt: 2 }}>
        <RadioGroup row value={mode} onChange={(e) => setMode(e.target.value)}>
          <FormControlLabel
            value="encrypt"
            control={<Radio />}
            label="Encrypt"
          />
          <FormControlLabel
            value="decrypt"
            control={<Radio />}
            label="Decrypt"
          />
        </RadioGroup>
      </FormControl>
      <Button
        variant="contained"
        color="primary"
        onClick={handleEncrypt}
        sx={{ mt: 2 }}
      >
        {mode === "encrypt" ? "Encrypt" : "Decrypt"}
      </Button>
      {result && (
        <Paper sx={{ mt: 4, p: 3 }}>
          <Typography variant="h6">Result</Typography>
          <Typography variant="body1">{result}</Typography>
        </Paper>
      )}
      {encryptionSteps.length > 0 && (
        <EncryptionSteps
          steps={encryptionSteps}
          activeStep={activeStep}
          setActiveStep={setActiveStep}
        />
      )}
    </Box>
  );
}

export default CipherForm;
