import React, { useState } from "react";
import { Box, TextField, Button } from "@mui/material";
import StepByStepView from "./StepByStepView";
import AES from "../algorithms/aes_class";

function CipherForm() {
  const [input, setInput] = useState("");
  const [key, setKey] = useState("");
  const [encryptionSteps, setEncryptionSteps] = useState(null);

  const handleEncrypt = () => {
    const aes = new AES();
    const { steps } = aes.encrypt(
      input.split("").map((c) => c.charCodeAt(0)),
      key.split("").map((c) => c.charCodeAt(0))
    );
    setEncryptionSteps(steps);
  };

  return (
    <Box sx={{ p: 3 }}>
      <TextField
        label="Input Text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        fullWidth
        margin="normal"
      />
      <TextField
        label="Key"
        value={key}
        onChange={(e) => setKey(e.target.value)}
        fullWidth
        margin="normal"
      />
      <Button variant="contained" onClick={handleEncrypt} sx={{ mt: 2 }}>
        Encrypt
      </Button>
      {encryptionSteps && <StepByStepView encryptionSteps={encryptionSteps} />}
    </Box>
  );
}

export default CipherForm;
